import { useContext } from "react";
import SignedinLayout from "../../components/layout/SignedinLayout";
import { Context } from "../../App";
import "./ComplaintsUser.css";
export default function ComplaintsUser() {
  const { currentUser } = useContext(Context);
  if (!JSON.parse(localStorage.getItem("complaints"))) {
  } else {
    let complaints = JSON.parse(localStorage.getItem("complaintss"));
    data.email = currentUser.email;
    complaints.push(data);
    localStorage.setItem("complaints", JSON.stringify(complaints));
    navigate("/user");
  }
  return (
    <>
      <SignedinLayout />
      <div className="user">
        <div>
          <p>
            Hello {currentUser.fullName.split(" ")[0]} ({currentUser.email})
          </p>
        </div>
        <div className="container">
          <h3>All Complaints</h3>

          <table>
            <th>
              <td>SUBJECT</td>
              <td>COMPLAINT TYPE</td>
              <td>COMPLAINT ID</td>
              <td>SEVERITY</td>
              <td>STATUS</td>
            </th>
            <tbody>
              {complaints.map((complaint, index) => (
                <tr key={index}>
                  <td>{complaint.subject}</td>
                  <td>{complaint.complaintType}</td>
                  <td>{complaint.complaintId}</td>
                  <td>{complaint.severity}</td>
                  <td>{complaint.status}</td>
                </tr>
              ))}
            </tbody>
          </table>
          <hr />
        </div>
      </div>
    </>
  );
}
