import "./AdminRegister.css";
import Layout from "../../components/layout/Layout.js";
import { useForm } from "react-hook-form";
export default function AdminRegister() {
  const {
    register,
    handleSubmit,
    formState: { errors },
    reset,
  } = useForm();
  const onSubmit = (data) => {
    if (!JSON.parse(localStorage.getItem("admins"))) {
      localStorage.setItem("admins", JSON.stringify([data]));
    } else {
      let admins = JSON.parse(localStorage.getItem("admins"));
      admins.map((admin) => {
        if (admin.email === data.email) {
          alert("Admin  with the same Email already exists");
        } else {
          admins.push(data);
          localStorage.setItem("admins", JSON.stringify(admins));
        }
      });
    }
    reset();
  };
  return (
    <>
      <Layout />
      <form onSubmit={handleSubmit(onSubmit)}>
        <div className="admin-reg">
          <h4>Admin Registration Form</h4>
          <br />
          <hr />
          <br />
          <div className="admin-form">
            <label>Full Name</label>
            <input
              {...register("fullName", { required: "Full Name is Required" })}
              type="text"
            />
            {errors.fullName && (
              <span className="error">{errors.fullName.message}</span>
            )}
            <label>Email</label>
            <input
              {...register("email", { required: "Email is Required" })}
              type="email"
            />
            {errors.email && (
              <span className="error">{errors.email.message}</span>
            )}
            <label>Password</label>
            <input
              {...register("password", { required: "Password is Required" })}
              type="password"
            />
            {errors.password && (
              <span className="error">{errors.password.message}</span>
            )}
            <label>Phone Number</label>
            <input
              {...register("phone", { required: "Phone is Required" })}
              type="tel"
            />
            {errors.phone && (
              <span className="error">{errors.phone.message}</span>
            )}
            <div className="checkbox">
              <input
                {...register("terms", {
                  required: "Agree to terms is Required",
                })}
                id="terms"
                type="checkbox"
              />
              <label htmlFor="terms">&nbsp;Agree to terms conditions</label>
            </div>{" "}
            {errors.terms && (
              <span className="error">{errors.terms.message}</span>
            )}
            <div className="reg">
              <button type="submit">Register</button>
            </div>
            <br />
          </div>
        </div>
      </form>
    </>
  );
}
